CREATE TABLE userconfigs_item (  conf_id smallint(5) unsigned NOT NULL auto_increment,  conf_modid smallint(5) unsigned NOT NULL default '0',  conf_uid smallint(5) unsigned NOT NULL default '0',  conf_name varchar(25) NOT NULL default '',  conf_title varchar(255) NOT NULL default '',  conf_value text,  conf_desc varchar(255) NOT NULL default '',  conf_formtype varchar(15) NOT NULL default '',  conf_valuetype varchar(10) NOT NULL default '',  conf_order smallint(5) unsigned NOT NULL default '0',  PRIMARY KEY  (conf_id),  KEY conf_mod_cat_id (conf_modid, conf_uid),  KEY conf_order (conf_order)) ENGINE=MyISAM;CREATE TABLE userconfigs_option (  confop_id mediumint(8) unsigned NOT NULL auto_increment,  confop_name varchar(255) NOT NULL default '',  confop_value varchar(255) NOT NULL default '',  conf_id smallint(5) unsigned NOT NULL default '0',  PRIMARY KEY  (confop_id),  KEY conf_id (conf_id)) ENGINE=MyISAM;